import numpy as np
import matplotlib.pyplot as plt
import math

def arena(N):
  # escriba su c√≥digo aqu√≠

  #Definimos el tama√±o del tablero-----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
  if N<32: #Caso N<32
    if N%2==0:
      m=N-1 #si es par necesito sacarle 1 para tener matriz imparXimpar
    else:
      m=N
  else:    #Caso N>32 con area de circulo
    m=int(int(math.sqrt(N/math.pi))+(math.sqrt(N/math.pi)*0.8))

  mat = np.zeros((m,m))

  #Procesamos los granos de arena-------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

  #Ya que todas las celdas vacias de la matriz son representadas por valor 0, y necesitamos matrices de tama√±o impar para tener un lugar central para los granos de arena
  #eso nos lleva a pensar que tomando divisi√≥n entera del tama√±o de la matriz nos permite colocar la arena al medio (EJ: 5x5 tomamos 5//2 y nos coloca los granos de arena en [2][2] que ser√≠a el centro de esa matriz)
  mat[m//2][m//2]=N

  #Recorremos toda la matriz partiendo desde arriba, ie, [0][0]
  #i=0
  #j=0
  cont=0 #Para contar numero de veces que se aplica la regla

  while revisamatriz(mat,m): #Modificada luego de feedback tarea #Funcion auxiliar que recorre la matriz y dice si hay valor 4 en alguna parte asi no cambie nada

    for i in range(0,m):
      for j in range(0,m):
        if mat[i][j]>=4:

          mat[i-1][j]+=1 #Agrega grano arriba
          mat[i][j-1]+=1 #Agrega grano izquierda
          mat[i+1][j]+=1 #Agrega grano abajo
          mat[i][j+1]+=1 #Agrega grano derecha

          mat[i][j]-=4 #le quitamos 4 granos de arena
          cont+=1


  print("Aplicaciones de la regla: ",cont) #printeamos numero de veces que se aplico la regla de distribuci√≥n
  #Dibuja
  plt.matshow(mat)
  plt.show()
  return cont

